sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	//statement stops execution if the field value change was caused by a template
	if (isLoading || newValue === '' || isTemplate) {
		//control is field you are selecting
		//oldValue is what was loaded into the field from the start
		//newValue is the value inputted
		//isLoading boolean value indicating whether the change is occurring as part of the form load.
		//isTemplate boolean telling you if the field was populated by a template.
		//console.log("Template change field");
		return;
	}
	//var alertTestUser = g_user;
	//console.log(alertTestUser);
	//alert("First Name: "+alertTestUser.firstName+"\nLast Name: "+alertTestUser.lastName+"\nUser ID: "+alertTestUser.userID);
	//Type appropriate comment here, and begin script below
	var whatNeeded = g_form.getValue("u_what_needed");
	g_form.clearOptions("u_what_needed");
	//Case switch for this one
	switch(newValue){
		case "hr": 
			g_form.addOption("u_what_needed","hr2","Human Resources 2"); 
			g_form.addOption("u_what_needed","other","Other");
			g_form.addOption("u_what_needed","hr1","helloworld"); 
			break;
		case "facilities":
			g_form.addOption('u_what_needed','facilities2','Facilities 2'); 
			g_form.addOption('u_what_needed','other','Other');
			g_form.addOption('u_what_needed','facilities1','test1'); 
			break;
		case "legal":
			g_form.addOption('u_what_needed','legal2','Legal 2'); 
			g_form.addOption('u_what_needed','other','Other');
			g_form.addOption('u_what_needed','legal1','Fubar'); 
			break;
	}

	if(isLoading && g_form.isNewRecord()){
		g_form.setValue("u_what_needed", whatNeeded);
	}
}
